from frontend.src.library.settings_helper.html_elements import status_card, status_card_style, status_card_header
from streamlit import columns, html, session_state
from frontend.src.library.settings_helper.client import fetch_apis_status
"""
Four states of Status
    Active: The API credentials are stored at the backend Server and connection works OK
    Unavailable: The API is not yet implemented
    Available: The API is available in the backend, but credentials have not yet been filled
    *Invalid Keys: API Keys have been given but don't work
"""


CRYPTO_APIS = {'Binance API': "https://www.logo.wine/a/logo/Binance/Binance-Vertical2-Dark-Background-Logo.wine.svg",
                'Binance Testnet API': "https://www.logo.wine/a/logo/Binance/Binance-Vertical2-Dark-Background-Logo.wine.svg",
                'Kraken API': "https://media.licdn.com/dms/image/D4E0BAQFm8yg0gGJN1A/company-logo_200_200/0/1697458897774/krakenfx_logo?e=2147483647&v=beta&t=CTWrqJakEEIx4lAPmddjLjt7e4Xi0HQqDTi7k9p3RPM",
                'Coinbase API': "https://alternative.me/media/256/coinbase-icon-kdtz42w4efva6qiu-c.png",
                'Coinbase Sandbox API': "https://back.gainium.io/uploads/Coinbase_Pro_d32bc1e094.jpeg"}

LLM_APIS = {'OpenAI API': "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSFJYCiV_aHxCRPoVcLeOChZWnb_qVxSQMm4Jr-C_x_0A&s",
                'Gemini API': "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQV1QWx0soc08N7wU8LjH95wZTkF_q13tg1KH4AOTs3xw&s",
                'Hugging Face API': "https://cdn.worldvectorlogo.com/logos/huggingface-2.svg"
            }
PRICE_API = {'CoinMarketCap API': "data:image/jpeg;base64,"}


def show_status_cards(only_exchange=False):
    status_json = fetch_apis_status("all")
    status_list = list(status_json.values())
    # EXCHANGE APIS
    global CRYPTO_APIS
    status_card_style()

    # if only_exchange:
    #     status_card_header("Exchange APIs")
    #     CRYPTO_APIS['Backend Server'] = "https://mdevelopers.com/storage/backend1_a9b6dc2f.png"
    #     statuses.append('Active')
    #     num_cols += 1
    html_txt = """<div class="section" id="apis">"""

    # api_cols = columns(4)
    i = 0
    for k, v in CRYPTO_APIS.items():
        html_txt += status_card(k, v, status_list[i], False)
        i += 1

    # LLM APIS
    # status_card_header("LLM APIs")
    for k, v in LLM_APIS.items():
        html_txt += status_card(k, v, status_list[i], False)
        i += 1

    # Price History APIS
    # status_card_header("LLM APIs")
    for k, v in PRICE_API.items():
        html_txt += status_card(k, v, status_list[i], False)
        i += 1
    html_txt += """</div>"""
    html(html_txt)
    return


def show_homepage_status_cards():
    global CRYPTO_APIS
    status_card_style()
    statuses = [session_state['backend_status'], session_state["binance_api_status"], session_state["binance_testnet_api_status"], session_state["kraken_api_status"], session_state["coinbase_api_status"], session_state["coinbase_sandbox_api_status"] ]

    i = 1
    html_txt = """<div class="section" id="apis">"""
    html_txt += status_card('Backend Server', "https://mdevelopers.com/storage/backend1_a9b6dc2f.png", statuses[0], False)
    for k, v in CRYPTO_APIS.items():
        html_txt += status_card(k, v, statuses[i],False)
        i += 1
    html_txt += """</div>"""
    html(html_txt)
    return
